INSERT INTO `questions` VALUES (1,'What is SEO ?','[\"A form of paid advertising\",\"Search Engine Optimization\",\"A social media platform\"]','Search Engine Optimization'),(2,'What is the main goal of content marketing?','[\"Increase direct sales\",\"Improve search engine rankings\",\"Engage and educate the audience\"]','Engage and educate the audience'),(3,'Which technique is used to target users who have already visited a website?','[\"SEO\",\"Remarketing\",\"Email marketing\"]','Remarketing'),(4,'Which of these is not a key factor in SEO?','[\"Number of followers on social networks\",\"Keywords\",\"Backlinks\"]','Number of followers on social networks'),(5,'What is PPC (Pay Per Click)?','[\"A content monetization model\",\"A type of advertising where the advertiser pays for each click\",\"A search engine ranking algorithm\"]','A type of advertising where the advertiser pays for each click'),(6,'What is an example of affiliate marketing?','[\"Paying for banner ads\",\"Receiving a commission for promoting other people\'s products\",\"Posting sponsored posts on Instagram\"]','Receiving a commission for promoting other people\'s products'),(7,'Why is social media marketing important?','[\"It allows tracking competitors\",\"It offers a platform for customer service\",\"It is only used for advertising campaigns\"]','It offers a platform for customer service'),(8,'What is a key performance indicator (KPI) for acquisition traffic?','[\"The number of shares on social networks\",\"Conversion rate\",\"The number of page views\"]','The number of page views'),(9,'What is influencer marketing?','[\"Using celebrities to promote products\",\"Data-based marketing\",\"Using opinion leaders to influence the target audience\"]','Using opinion leaders to influence the target audience'),(10,'What is \'content marketing\'?','[\"The creation and distribution of relevant and useful content to attract an audience\",\"Selling content online\",\"Publishing advertisements in newspapers\"]'